<template>
    <div class="flex items-start relative ml-2">
        <input
            type="checkbox"
            class="w-12 h-12 z-10 opacity-0 "
            ref="checkbox"
            :value="checked"
            @input="$emit('update:modelValue', $event.target.checked)"
            @keyup.enter="$emit('update:modelValue', !checked)"
        />
        <div
            class="w-12 h-12 checkbox_placeholder absolute top-0 left-0 right-0 bottom-0 z-0 hover:cursor-pointer flex items-center justify-center"
            :class="{ 'bg-checked': checked, 'bg-unchecked': !checked }"
            @click="$refs.checkbox.click()"
        >
            <svg
                width="25"
                height="20"
                viewBox="0 0 25 20"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
                xmlns:xlink="http://www.w3.org/1999/xlink"
            >
                <rect width="25" height="20" fill="url(#pattern0)" />
                <defs>
                    <pattern
                        id="pattern0"
                        patternContentUnits="objectBoundingBox"
                        width="1"
                        height="1"
                    >
                        <use
                            xlink:href="#image0_3_2137"
                            transform="translate(0 -0.00351476) scale(0.00111359 0.00146158)"
                        />
                    </pattern>
                    <image
                        id="image0_3_2137"
                        width="898"
                        height="689"
                        xlink:href="data:image/png;base64,"
                    />
                </defs>
            </svg>
        </div>
    </div>
</template>

<script>
export default {
    name: 'SupexCheckbox',
    props: {
        modelValue: {
            type: Boolean,
            default: false,
            required: true
        }
    },
    data() {
        return {
            checked: this.modelValue,
        }
    },
    watch: {
        modelValue: function (newVal) {
            this.checked = newVal
        }
    }
}
</script>

<style scoped>
.checkbox_placeholder {
    border-radius: 0.3rem;
}

.bg-checked {
    background: #38f7b2 !important;
    transition: all .1s;
}

.bg-unchecked {
    background: #eafaf6;
    transition: all .1s;
}

input:hover {
    cursor: pointer;
    background: #a6f5d8;
    transition: all .1s;
}

input:focus + div.checkbox_placeholder {
    background: #a6f5d8;
    transition: all .1s;
}
</style>
